/*---------TABS----------*/
let filterMenu = document.querySelectorAll(".filter-btn");
let filterMain = document.querySelector(".filter-main");

if (filterMain != null) {
    filterMain.addEventListener("click", () => {
        let items = document.querySelectorAll(".filter-item");

        for (let i = 0; i < items.length; i++) {
            items[i].classList.toggle("hide");
        }
    });

    for (let i = 0; i < filterMenu.length; i++) {
        filterMenu[i].addEventListener("click", function filter(e) {
            this.parentNode.querySelector(".filter-menu").classList.toggle("active");
        });
    }
}
